import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || '';

// Create a separate axios instance for public endpoints
const publicApi = axios.create({
  baseURL: 'http://localhost:3000',  // Sửa lại baseURL để match với backend
  headers: {
    'Content-Type': 'application/json',
    'Access-Control-Allow-Origin': '*'
  },
  withCredentials: false  // Tắt withCredentials vì backend không yêu cầu
});

export const ritualService = {
  getAllRituals: () => publicApi.get('/api/ritual'),  // Sửa lại endpoint
  getRitualById: (id) => publicApi.get(`/api/ritual/${id}`),  // Sửa lại endpoint
  getRitualsByRegion: (region) => publicApi.get(`/api/ritual/region/${region}`),  // Sửa lại endpoint
  searchRituals: (name) => publicApi.get(`/api/ritual/search?name=${name}`)  // Sửa lại endpoint và tham số query
};
