import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { ritualService } from "../services/ritualService";
import { formatSolarDate } from "../utils/dateUtils";

const BACKEND_BASE = "https://isp-7jpp.onrender.com";

const RitualDetail = () => {
  const { id } = useParams();
  const [ritual, setRitual] = useState(null);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");

  useEffect(() => {
    const fetchDetail = async () => {
      try {
        setLoading(true);
        setErr("");
        const res = await ritualService.getRitualById(id);
        const data = res?.data || res; // tu·ª≥ service tr·∫£ g√¨
        console.log("üéØ Chi ti·∫øt l·ªÖ h·ªôi (API):", data);
        setRitual(data);
      } catch (e) {
        console.error("‚ùå L·ªói chi ti·∫øt l·ªÖ h·ªôi:", e);
        setErr(e?.message || "L·ªói kh√¥ng x√°c ƒë·ªãnh");
      } finally {
        setLoading(false);
      }
    };
    fetchDetail();
  }, [id]);

  const getImageUrl = (u) => {
    if (!u) return "https://images.unsplash.com/photo-1578662996442-48f60103fc96?w=900";
    return u.startsWith("http") ? u : `${BACKEND_BASE}${u}`;
  };

  if (loading) {
    return (
      <div className="min-h-[60vh] flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-vietnam-red"></div>
      </div>
    );
  }

  if (err) {
    return (
      <div className="max-w-3xl mx-auto py-12 text-center text-red-600">
        C√≥ l·ªói khi t·∫£i chi ti·∫øt: {err}
      </div>
    );
  }

  if (!ritual) {
    return (
      <div className="max-w-3xl mx-auto py-12 text-center text-gray-600">
        Kh√¥ng t√¨m th·∫•y th√¥ng tin l·ªÖ h·ªôi.
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-vietnam-cream py-8 px-4">
      <div className="max-w-5xl mx-auto bg-white rounded-xl shadow-lg overflow-hidden">
        <img
          src={getImageUrl(ritual.imageUrl)}
          alt={ritual.ritualName}
          className="w-full h-[380px] object-cover"
        />

        <div className="p-6 md:p-8">
          <h1 className="text-3xl md:text-4xl font-serif font-bold text-vietnam-red mb-4">
            {ritual.ritualName}
          </h1>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
            <div className="bg-vietnam-cream p-4 rounded-lg">
              <p className="font-semibold text-vietnam-red">V√πng mi·ªÅn</p>
              <p>{ritual.regionName}</p>
            </div>
            <div className="bg-vietnam-cream p-4 rounded-lg">
              <p className="font-semibold text-vietnam-red">√Çm l·ªãch</p>
              <p>{ritual.dateLunar || "‚Äî"}</p>
            </div>
            <div className="bg-vietnam-cream p-4 rounded-lg">
              <p className="font-semibold text-vietnam-red">D∆∞∆°ng l·ªãch</p>
              <p>{formatSolarDate(ritual.dateSolar)}</p>
            </div>
          </div>

          <h3 className="text-xl font-serif font-bold text-vietnam-gold mb-2">M√¥ t·∫£</h3>
          <p className="text-gray-700 leading-relaxed whitespace-pre-line mb-4">
            {ritual.description || "‚Äî"}
          </p>

          {ritual.meaning && (
            <>
              <h3 className="text-xl font-serif font-bold text-vietnam-gold mb-2">√ù nghƒ©a</h3>
              <p className="text-gray-700 leading-relaxed whitespace-pre-line">
                {ritual.meaning}
              </p>
            </>
          )}

          <div className="mt-8 text-center">
            <Link to="/" className="bg-vietnam-red text-white px-6 py-3 rounded-md hover:bg-red-700">
              ‚Üê Quay l·∫°i trang ch·ªß
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RitualDetail;
