// src/pages/RitualDetail.js
import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import Navbar from "../components/Navbar";      // có thể lỗi nếu thiếu Provider
import Footer from "../components/Footer";      // có thể lỗi nếu thiếu Provider
import { ritualService } from "../services/ritualService";
import { formatSolarDate } from "../utils/dateUtils";
import { scrollToTop } from "../utils/scrollUtils";

const BACKEND_BASE = "https://isp-7jpp.onrender.com";

/* ---------- Error Boundary: chặn crash khi thiếu Provider ---------- */
class SafeBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false };
  }
  static getDerivedStateFromError() {
    return { hasError: true };
  }
  componentDidCatch(err) {
    // Không log ầm ĩ, chỉ giữ trang chạy tiếp
    console.warn("[SafeBoundary] Fallback vì thiếu Provider:", err?.message);
  }
  render() {
    if (this.state.hasError) {
      return this.props.fallback || null;
    }
    return this.props.children;
  }
}

/* ---------- Header/Footer đơn giản (không dùng context) ---------- */
const SimpleHeader = () => (
  <header className="bg-vietnam-green text-white">
    <div className="max-w-7xl mx-auto px-4 py-3 flex items-center justify-between">
      <Link to="/" className="font-bold text-lg">Nhắc Nhớ Việt</Link>
      <nav className="flex gap-4 text-sm md:text-base">
        <Link to="/" className="hover:underline">Trang chủ</Link>
        <Link to="/rituals" className="hover:underline">Tra cứu lễ</Link>
        <Link to="/cart" className="hover:underline">Giỏ hàng</Link>
        <Link to="/login" className="hover:underline">Đăng nhập</Link>
        <Link to="/register" className="hover:underline">Đăng ký</Link>
      </nav>
    </div>
  </header>
);

const SimpleFooter = () => (
  <footer className="bg-vietnam-green text-white mt-10">
    <div className="max-w-7xl mx-auto px-4 py-6 text-sm md:text-base flex flex-col md:flex-row gap-3 md:items-center justify-between">
      <span>© {new Date().getFullYear()} Nhắc Nhớ Việt</span>
      <div className="flex gap-4">
        <Link to="/" className="hover:underline">Trang chủ</Link>
        <Link to="/rituals" className="hover:underline">Tra cứu lễ</Link>
        <Link to="/cart" className="hover:underline">Giỏ hàng</Link>
      </div>
    </div>
  </footer>
);

/* ----------------------- Trang chi tiết ----------------------- */
const RitualDetail = () => {
  const { id } = useParams();
  const [ritual, setRitual] = useState(null);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");

  useEffect(() => {
    scrollToTop(true);
    const fetchDetail = async () => {
      try {
        setLoading(true);
        setErr("");
        const res = await ritualService.getRitualById(id);
        const data = res?.data || res;
        setRitual(data);
      } catch (e) {
        setErr(e?.message || "Lỗi không xác định");
      } finally {
        setLoading(false);
      }
    };
    fetchDetail();
  }, [id]);

  const getImageUrl = (u) => {
    if (!u) return "https://images.unsplash.com/photo-1578662996442-48f60103fc96?w=1200";
    return u.startsWith("http") ? u : `${BACKEND_BASE}${u}`;
  };

  return (
    <>
      {/* Thử render Navbar gốc; nếu thiếu Provider -> dùng SimpleHeader */}
      <SafeBoundary fallback={<SimpleHeader />}>
        <Navbar />
      </SafeBoundary>

      {loading ? (
        <div className="min-h-[60vh] flex items-center justify-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-vietnam-red"></div>
        </div>
      ) : err ? (
        <div className="max-w-3xl mx-auto py-16 px-4 text-center text-red-600 text-lg">
          Có lỗi khi tải chi tiết: {err}
        </div>
      ) : !ritual ? (
        <div className="max-w-3xl mx-auto py-16 px-4 text-center text-gray-600 text-lg">
          Không tìm thấy thông tin lễ hội.
        </div>
      ) : (
        <div className="bg-vietnam-cream py-10 px-4">
          <div className="max-w-6xl mx-auto bg-white rounded-2xl shadow-lg overflow-hidden">
            {/* Ảnh lớn */}
            <img
              src={getImageUrl(ritual.imageUrl)}
              alt={ritual.ritualName}
              className="w-full h-[420px] object-cover"
            />

            <div className="p-6 md:p-10">
              {/* Tiêu đề lớn */}
              <h1 className="text-4xl md:text-5xl font-serif font-bold text-vietnam-deep mb-6 leading-tight">
                {ritual.ritualName}
              </h1>

              {/* Thông tin tóm tắt */}
              <div className="grid grid-cols-1 md:grid-cols-3 gap-5 mb-8">
                <div className="bg-vietnam-cream p-5 rounded-xl">
                  <p className="text-vietnam-red font-semibold text-lg mb-1">Vùng miền</p>
                  <p className="text-lg">{ritual.regionName}</p>
                </div>
                <div className="bg-vietnam-cream p-5 rounded-xl">
                  <p className="text-vietnam-red font-semibold text-lg mb-1">Âm lịch</p>
                  <p className="text-lg">{ritual.dateLunar || "—"}</p>
                </div>
                <div className="bg-vietnam-cream p-5 rounded-xl">
                  <p className="text-vietnam-red font-semibold text-lg mb-1">Dương lịch</p>
                  <p className="text-lg">{formatSolarDate(ritual.dateSolar)}</p>
                </div>
              </div>

              {/* Mô tả */}
              <h2 className="text-2xl md:text-3xl font-serif font-bold text-vietnam-gold mb-3">
                Mô tả
              </h2>
              <p className="text-[17px] md:text-lg text-gray-800 leading-8 whitespace-pre-line mb-8">
                {ritual.description || "—"}
              </p>

              {/* Ý nghĩa */}
              {ritual.meaning && (
                <>
                  <h2 className="text-2xl md:text-3xl font-serif font-bold text-vietnam-gold mb-3">
                    Ý nghĩa
                  </h2>
                  <p className="text-[17px] md:text-lg text-gray-800 leading-8 whitespace-pre-line">
                    {ritual.meaning}
                  </p>
                </>
              )}

              {/* Nút quay lại */}
              <div className="mt-10 text-center">
                <Link
                  to="/"
                  className="inline-flex items-center justify-center px-6 py-3 rounded-lg
                             bg-vietnam-green text-white text-base md:text-lg
                             hover:brightness-110 transition"
                >
                  ← Quay lại trang chủ
                </Link>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Thử render Footer gốc; nếu thiếu Provider -> dùng SimpleFooter */}
      <SafeBoundary fallback={<SimpleFooter />}>
        <Footer />
      </SafeBoundary>
    </>
  );
};

export default RitualDetail;
